mapper.select:
SELECT O.ORDER_NO,O.PRODUCT_NO,P.PRODUCT_NAME,O.QUANTITY,O.ID,O.PURCHASED_TIME
FROM PRODUCT_ORDER O, PRODUCT P
WHERE P.PRODUCT_NO=O.PRODUCT_NO and O.ID=#{value}

SELECT RNUM,M.ID,M.PASSWORD,M.NAME,M.TEL,M.BIRTH,M.SEX,M.EMAIL,M.ADDRESS,M.LOGIN_TIME,M.LOGIN_FAIL,M.POINT, M.SIGNUP_DATE,M.AGREEMENT,M.ACC_STATUS_NO,A.ACC_STATUS_INFO,M.REPORTCOUNT
FROM (SELECT ROW_NUMBER() OVER(ORDER BY ID ASC) AS RNUM,ID,PASSWORD,NAME,TEL,BIRTH,SEX,EMAIL,ADDRESS,LOGIN_TIME,LOGIN_FAIL, POINT,TO_CHAR(SIGNUP_DATE, 'YYYY-MM-DD') AS SIGNUP_DATE,AGREEMENT,ACC_STATUS_NO ,REPORTCOUNT
FROM MEMBER )M, ACC_STATUS A WHERE M.ACC_STATUS_NO = A.ACC_STATUS_NO AND RNUM BETWEEN #{startRowNumber} AND #{endRowNumber}
